import $ from 'jquery'
import delay from './delay'
// It uses setTimeout to simulate the delay of an AJAX call.

// All calls return promises.
/**
 * Account Class for handling account APIs
 * @class AccountApi
 */
class AccountApi {
    static login(user) {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                $.post(account.login, user)
                    .then((response) => {
                        if (response.status === "fail") {
                            reject(response.error)
                        } else {
                            delete response.player.password
                            response.player.username = user.username
                            resolve(response)
                        }
                    }).catch(reject)
            }, delay)
        })
    }

    static logout(user) {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                $.post(account.logout, user)
                    .then((response) => {
                        if (response.status === "fail") {
                            reject(response.error)
                        } else {
                            resolve(response)
                        }
                    }).catch(reject)
            }, delay)
        })
    }
}

export default AccountApi

import { game } from './apiUrl'
import $ from 'jquery'
import delay from './delay'
// It uses setTimeout to simulate the delay of an AJAX call.

// All calls return promises.
/**
 * GameApi Class for handling game APIs
 * @class GameApi
 */
class GameApi {
    static getAllGameCategories() {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                $.get(game.categories)
                    .then(resolve)
                    .catch(reject)
            }, delay)
        })
    }

    static getAllGames(keyword, categoryIds = []) {
        keyword = keyword && keyword.toLowerCase()
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                $.get(game.games)
                    .then(games => {
                        if (!keyword) {
                            return games
                        } else {
                            return games.filter(game => {
                                return game
                                    .name
                                    .toLowerCase()
                                    .includes(keyword)
                                    ||
                                    game
                                        .description
                                        .toLowerCase()
                                        .includes(keyword)
                            })
                        }
                    })
                    .then(games => {
                        if (categoryIds.length === 0) {
                            return games
                        } else {
                            return games
                                .filter(game => {
                                    return categoryIds
                                        .filter(cId => {
                                            return game.categoryIds.includes(cId)
                                        }).length > 0
                                })
                        }
                    }).then(resolve)
                    .catch(reject)
            }, delay)
        })
    }
}

export default GameApi

